import React, { Component } from 'react';
import { Container, Card, ListGroup, Button} from 'react-bootstrap';



const initialNotes = [
    {intext : 'hello i am a first note'},
    {intext : 'i am the second one'},
    {intext : 'i third'},
    {intext : 'yeehawww'}    
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');



    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd() {
        const newList = notes.concat({ intext: text});
        setNote(newList);

        setText('');      
    }

    function Note(props) {
        return <li>{props.intext}
        <button className="btn-danger ml-2" onClick={() => props.deleteNote(props.id)}>
        <b>X</b>
        </button>
        </li>
      }

    const deleteNote = (id) => {
        //console.log(id);
        notes.map(note=>console.log(note.id));
        //console.log(notes.filter((note) => note.id !== id))
    }

    return ( 
    <div>
        <Container fluid>
        <Card
            bg={'info'}
            style={{ width: '18rem' }}
            className='rounded-0 mb-2 text-center'
        >
        <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
        <div>
        <input type="text" value={text} onChange={handleChange} />
        <button type="button" onClick={handleAdd}>
          Add
        </button>
        </div>

        <Card.Body>
        <ul>
            {notes.map((element, id) => <Note key = {id.toString()} intext ={element.intext} id = {id} deleteNote = {deleteNote}></Note>)}
        </ul>
        </Card.Body>
        </Card>
        </Container>
    </div>
    );
}

export default Far;
