import React from 'react';
import { Container, Card, ListGroup, ListGroupItem, Button, Form, Col} from 'react-bootstrap';
import { v4 } from 'uuid';
import Rectangle from 'react-rectangle';
import Draggable from 'react-draggable';
import 'bootstrap/dist/css/bootstrap.min.css';





const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
    {intext : 'i am the second one', id: v4()},
    {intext : 'i third', id: v4()},
    {intext : 'yeehawww', id: v4()}    
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');


    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd() {
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');
    }

    function Note(props) {
        return <ListGroupItem style={{ color: "black", background: "#7788F2"}}>
        {props.intext}
        <Button variant="outline-danger" size ="sm" onClick={() => props.deleteNote(props.id)}>X</Button>
        </ListGroupItem>      }


    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }

    return ( 
        <Container>
        {/* <Card style={{ color: "white", background: "#0EEEAB", width: '20rem'}}
            className='rounded-0 mb-2 text-center'
        >
        <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
        <div>
        <Form.Control size="sm" type="text" value={text} onChange={handleChange}/>
        <Button className="btn btn-block" variant="dark" size ="sm" onClick={handleAdd}> Add Note</Button>
        </div>

        <Card.Body>
        <ListGroup className="list-group-flush">
            {notes.map((element) => <Note key = {element.id.toString()} intext ={element.intext} id = {element.id} deleteNote = {deleteNote}></Note>)}
        </ListGroup>
        </Card.Body>
        </Card> */}

        <div style={{ background: '#7788F2', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle className="text-center" style = {{overflow: 'auto'}}>
            <div class="form-group">
            <h3 style={{ color: 'black' }}>What the world <br></br><b>NEEDS</b></h3>
            </div>

            <p>
            {notes.map((element) => <Note key = {element.id.toString()} intext ={element.intext} id = {element.id} deleteNote = {deleteNote}></Note>)}
            </p>

            <Col lg = {7}>
            <Form.Control value={text} onChange={handleChange}/>
            </Col>

        </Rectangle>
        </div>

        <div style={{ background: '#0EEEAB', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle className="text-center">
        <h3 style={{ color: '#black' }}>What you<br></br><b>LOVE</b></h3>

        </Rectangle>
        </div>

        <div style={{ background: '#FF8584', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle>

        </Rectangle>
        </div>

        <div style={{ background: '#FFDF00', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle>


        </Rectangle>
        </div>

        </Container>



    );
}

export default Far;
