import React, { Component } from 'react';
import { Container, Card, ListGroup, Button, Form} from 'react-bootstrap';
import { v4 } from 'uuid';



const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
    {intext : 'i am the second one', id: v4()},
    {intext : 'i third', id: v4()},
    {intext : 'yeehawww', id: v4()}    
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');



    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd() {
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');      
    }

    function Note(props) {
        return <li>{props.intext}
        <Button className="ml-3" variant="outline-danger" size ="sm" onClick={() => props.deleteNote(props.id)}>X</Button>
        </li>
      }

    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }

    return ( 
    <div>
        <Container fluid>
        <Card
            bg={'info'}
            style={{ width: '18rem' }}
            className='rounded-0 mb-2 text-center'
        >
        <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
        <div>
        <Form.Control size="sm" type="text" placeholder="Enter your note here" />
        <Button className="btn btn-block" variant="success" size ="sm">Add Note</Button>
        <input type="text" value={text} onChange={handleChange} />
        <button type="button" onClick={handleAdd}>
          Add
        </button>
        </div>

        <Card.Body>
        <ul>
            {notes.map((element, id) => <Note key = {element.id.toString()} intext ={element.intext} id = {element.id} deleteNote = {deleteNote}></Note>)}
        </ul>
        </Card.Body>
        </Card>
        </Container>
    </div>
    );
}

export default Far;
