import React from 'react';
import { Container, Card, ListGroup, ListGroupItem, Button, Form, Col, Row} from 'react-bootstrap';
import { v4 } from 'uuid';
import Rectangle from 'react-rectangle';
import Draggable from 'react-draggable';
import 'bootstrap/dist/css/bootstrap.min.css';



const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');

    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd(event) {
        if (!text){
            return;
        }
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');
    }

    function Note(props) {
        return <Button style = {{marginTop: '10px'}} variant = 'dark' className = 'rounded-pill'>
        {props.intext}
        <Button variant="danger" size ="sm" onClick={() => props.deleteNote(props.id)}>X</Button>
        </Button>
        }


    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }

    return ( 
        <Container style = {{width: '60rem'}}>
            <Draggable>
        <div style={{ background: '#7788F2', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle className="text-center" style = {{overflow: 'scroll'}}>
            <h3 style={{ color: 'black' }}>What the world <br></br><b>NEEDS</b></h3>
            <Container style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-around', flexWrap: 'wrap'}}>
            {notes.map((element) => <Note key = {element.id.toString()} intext ={element.intext} id = {element.id} deleteNote = {deleteNote}></Note>)}
            </Container>
            <Container>
            <Form.Control className = 'rounded-pill' value={text} onChange={handleChange} onBlur={handleAdd} style={{width: '10rem'}}/>
            </Container>
        </Rectangle>
        </div>
        </Draggable>

        <div style={{ background: '#0EEEAB', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle className="text-center">
        <h3 style={{ color: '#black' }}>What you<br></br><b>LOVE</b></h3>

        </Rectangle>
        </div>

        <div style={{ background: '#FF8584', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle>

        </Rectangle>
        </div>

        <div style={{ background: '#FFDF00', width: '20vw', height: '40vh', float: 'left'}}>
        <Rectangle>


        </Rectangle>
        </div>

        </Container>



    );
}

export default Far;
