    //   };


    return ( 
    <div>
        <Container fluid>
        <Card
            bg={'info'}
            style={{ width: '18rem' }}
            className='rounded-0 mb-2 text-center'
        >
        <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
        <div>
        <input type="text" onChange={handleChange} />
        <button type="button" onClick={handleAdd}>
          Add
        </button>
        </div>

        <Card.Body>
        <Card.Text>
        <ul>
            <div class="col-md-12">
            <div class="form-group">
            <label for="nameText" class="control-label"></label>
            <div class="input-group">
            <input id="nameText" type="text" class="form-control" value = {notes[0].intext}></input>
            <div class="input-group-btn">
            <button type="button" class="btn btn-success">
            <b>OK</b>
            </button>
            </div>
            </div>
            </div>
            </div>
            {notes.map((element, id) => <Note key = {id.toString()} intext ={element.intext} id = {id}></Note>)}
        </ul>

        </Card.Text>
        </Card.Body>
        </Card>
        </Container>
    </div>
    );
}

export default Far;
