import React, { Component } from 'react';
import { Container, Card, ListGroup, Button} from 'react-bootstrap';

function Note(props) {
    return <li>{props.intext}
    <button type="button" class="btn btn-danger-sm">
    <b>X</b>
    </button>
    </li>
  }


class Far extends Component {
     handleChange = event => {
        this.setState({ username: event.target.value });
      };

    state = {
        notes : [
            {intext : 'hello i am a first note'},
            {intext : 'i am the second one'},
            {intext : 'i third'},
            {intext : 'yeehawww'}    
        ]
    }

    addNote = note => {
        this.setState(prevState => ({
          notes: [...prevState.notes, note]
        }));
      };

    render() { 
        return ( 
        <div>
            <Container fluid>
            <Card
                bg={'info'}
                style={{ width: '18rem' }}
                className='rounded-0 mb-2 text-center'
            >
            <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
            <Button onClick = {this.addNote}>Add</Button>
            <Card.Body>
            <Card.Text>
            <ul>
                <div class="col-md-12">
                <div class="form-group">
                <label for="nameText" class="control-label"></label>
                <div class="input-group">
                <input id="nameText" type="text" value = {this.state.notes[0].intext}></input>
                <div class="input-group-btn">
                <button type="button" class="btn btn-success">
                <b>OK</b>
                </button>
                </div>
                </div>
                </div>
                </div>
                {this.state.notes.map((element, id) => <Note key = {id.toString()} intext ={element.intext} id = {id}></Note>)}
            </ul>

            </Card.Text>
            </Card.Body>
            </Card>
            </Container>
        </div>
        );
    }
}

export default Far;
