import React, { Component } from 'react';
import { Container, Card, ListGroup, Button} from 'react-bootstrap';
import { v4 } from 'uuid';

function Note(props) {
    return <li>{props.intext}
    <button type="button" class="btn btn-sm">
    <b>X</b>
    </button>
    </li>
  }

const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
    {intext : 'i am the second one', id: v4()},
    {intext : 'i third', id: v4()},
    {intext : 'yeehawww', id: v4()}    
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');

    //  handleChange = event => {
    //     this.setState({ username: event.target.value });
    //   };

    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd() {
        const newList = notes.concat({ intext: text, id: v4() });
        setNote(newList);

        setText('');      
    }

    // function addNote = note => {
    //     this.setState(prevState => ({
    //       notes: [...prevState.notes, note]
    //     }));
    //   };


    return ( 
    <div>
        <Container fluid>
        <Card
            bg={'info'}
            style={{ width: '18rem' }}
            className='rounded-0 mb-2 text-center'
        >
        <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
        <div>
        <input type="text" value={text} onChange={handleChange} />
        <button type="button" onClick={handleAdd}>
          Add
        </button>
        </div>

        <Card.Body>
        <Card.Text>
        <ul>
            {notes.map((element, id) => <Note key = {id.toString()} intext ={element.intext} id = {id}></Note>)}
        </ul>

        </Card.Text>
        </Card.Body>
        </Card>
        </Container>
    </div>
    );
}

export default Far;
