import React, { Component } from 'react';
import { Container, Card, ListGroup, ListGroupItem, Button, Form} from 'react-bootstrap';
import { v4 } from 'uuid';



const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
    {intext : 'i am the second one', id: v4()},
    {intext : 'i third', id: v4()},
    {intext : 'yeehawww', id: v4()}    
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');



    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd() {
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');      
    }

    function Note(props) {
        return <ListGroupItem style={{ color: "black", background: "#edb7cc"}}>
        {props.intext}
        <Button className="align-right" variant="outline-danger" size ="sm" onClick={() => props.deleteNote(props.id)}>X</Button>
        </ListGroupItem>      }


    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }

    return ( 
        <Container variant = "justify-content-center">
        <Card style={{ color: "white", background: "#edb7cc", width: '20rem'}}
            className='rounded-0 mb-2 text-center'
        >
        <Card.Header>What the world <br></br><b>NEEDS</b></Card.Header>
        <div>
        <Form.Control size="sm" type="text" placeholder="Enter your note here" value={text} onChange={handleChange}/>
        <Button className="btn btn-block" variant="dark" size ="sm" onClick={handleAdd}> Add Note</Button>
        </div>

        <Card.Body>
        <ListGroup className="list-group-flush">
            {notes.map((element, id) => <Note key = {element.id.toString()} intext ={element.intext} id = {element.id} deleteNote = {deleteNote}></Note>)}
        </ListGroup>
        </Card.Body>
        </Card>
        </Container>

    );
}

export default Far;
