import React from 'react';
import { Container, Card, ListGroup, ListGroupItem, Button, Form, Col, Row} from 'react-bootstrap';
import { v4 } from 'uuid';
import Rectangle from 'react-rectangle';
import Draggable from 'react-draggable';
import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css';


const initialNotes = [
    {intext : 'hello i am a first note', id: v4()},
]

const Far = ()=> {
    const [notes, setNote] = React.useState(initialNotes);
    const [text, setText] = React.useState('');

    function handleChange(event) {
        setText(event.target.value);
      }

    function handleAdd(event) {
        if (!text){
            return;
        }
        const newList = notes.concat({ intext: text, id: v4()});
        setNote(newList);

        setText('');
    }

    function Note(props) {
        return <div style = {{marginBottom: '5px'}} variant = 'light' className = 'rounded-pill'>
            {props.intext}
            <Button className="btn-delete" variant="danger" size ="sm" onClick={() => props.deleteNote(props.id)}>X</Button>
        </div>
        }


    const deleteNote = (id) => {
        setNote(notes.filter((note) => note.id !== id))
    }

    return ( 
            <div className="rectangle-container">
                <div className="text-center" style = {{overflow: 'scroll'}}>
                    <h3 className="dark-blue">What the world <br></br><b>NEEDS</b></h3>
                    <Container className="pill-container">
                        {notes.map((element) => <Note key = {element.id.toString()} intext ={element.intext} id = {element.id} deleteNote = {deleteNote}></Note>)}
                    </Container>
                    <Container>
                        <Form.Control className = 'rounded-pill' value={text} onChange={handleChange} onBlur={handleAdd} style={{width: '10rem'}}/>
                    </Container>
                </div>
            </div>


    );
}

export default Far;
